mapper.insert:

INSERT INTO education (
	emp_no
	, title
	, content
	, place
	, edu_date
	, capacity
) VALUE (
	#{empNo}, #{title}, #{content}, #{place}, #{eduDate}, #{capacity}
)

INSERT INTO education_file (
	edu_no
	, origin_name 
	, file_name
	, ext
	, kind
	, size
) VALUES (
	#{eduNo}, #{originName}, #{fileName}, #{ext}, #{kind}, #{size}
)
