mapper.insert:
INSERT INTO course (
	emp_no empNo
	, title
	, content
	, place
	, edu_date eduDate
	, capacity
) VALUE (
	#{empNo}, #{title}, #{content}, #{place}, #{eduDate}, #{capacity}
)

INSERT INTO education_file (
	edu_no eduNo
	, origin_name originName 
	, file_name fileName
	, ext
	, kind
	, size
) VALUES (
	#{eduNo}, #{originName}, #{fileName}, #{ext}, #{kind}, #{size}
)
