mapper.select:
select d.dra_no draNo , d.kind type , kc.descript draftType , d.title title , date_format(d.create_date, '%y년%c월%e일 %H시%i분') createDate , sc.descript approvalState from draft d inner join approval a on d.dra_no = a.dra_no inner join refer r on a.app_no = r.app_no inner join common_code kc on d.kind = kc.common_code inner join common_code sc on a.state = sc.common_code where r.emp_no = #{empNo}
and (d.title like concat ('%', #{search}, '%') or kc.descript like concat ('%', #{search}, '%') or sc.descript like concat ('%', #{search}, '%'))
order by d.dra_no desc limit #{start} , #{length}

select count(*)
  from refer
 where emp_no = #{empNo}

select d.dra_no draNo , d.kind type , kc.descript draftType , d.title title , date_format(d.create_date, '%y년%c월%e일 %H시%i분') createDate , sc.descript approvalState from draft d inner join approval a on d.dra_no = a.dra_no inner join common_code kc on d.kind = kc.common_code inner join common_code sc on a.state = sc.common_code where a.mid_app = #{empNo} or a.final_app = #{empNo}
and (d.title like concat ('%', #{search}, '%') or kc.descript like concat ('%', #{search}, '%') or sc.descript like concat ('%', #{search}, '%'))
order by d.dra_no desc limit #{start} , #{length}

select count(*)
  from approval
 where mid_app = #{empNo}
	or final_app = #{empNo}

select d.dra_no draNo , d.kind type , kc.descript draftType , d.title title , date_format(d.create_date, '%y년%c월%e일 %H시%i분') createDate , sc.descript approvalState from draft d inner join approval a on d.dra_no = a.dra_no inner join common_code kc on d.kind = kc.common_code inner join common_code sc on a.state = sc.common_code where d.emp_no = #{empNo}
and (d.title like concat ('%', #{search}, '%') or kc.descript like concat ('%', #{search}, '%') or sc.descript like concat ('%', #{search}, '%'))
order by draNo desc limit #{start} , #{length}

select count(*)
  from draft
 where emp_no = #{empNo}
