mapper.insert:
INSERT INTO msg (
	title, content
) VALUES (
	#{title}, #{content}
)

INSERT INTO msg_file (
	  msg_no
	, origin_name 
	, file_name
	, ext
	, kind
	, size
) VALUES (
	#{msgNo}, #{originName}, #{fileName}, #{ext}, #{kind}, #{size}
)

INSERT INTO msg_sender ( msg_no , emp_no , state , send_date ) VALUES ( #{msgNo}, #{empNoS}, #{stateS},
null now()
)

INSERT INTO msg_reader (
	  msg_no
	, emp_no
) VALUES (
	#{msgNo}, #{empNoR}
)

mapper.update:
UPDATE msg_reader
SET state = "D02", read_date = now()
WHERE msg_no = #{msgNo}
