mapper.select:
select emp_no empNo
from emp
where emp_no = #{empNo}

select	count(*)
  from emp
 where emp_no = #{empNo}
   and email = #{email}

mapper.update:
update emp
set emp_pw = #{empPw}
where emp_no = #{empNo}

UPDATE emp


		dep_no = #{depNo},


		location = #{location},


		ext_num = #{extNum}


WHERE emp_no = #{empNo}

UPDATE emp
SET leave_date = #{leaveDate}
WHERE emp_no = #{empNo}

	UPDATE emp
   	   SET total_leave = #{totalLeave}, used_leave=0
   	 WHERE emp_no = #{empNo}

	UPDATE emp
   	   SET total_leave = #{totalLeave}
   	 WHERE emp_no = #{empNo}

	UPDATE emp
	   SET connection_status = #{connectionStatus}
   	 WHERE emp_no = #{empNo}
